#include<bits/stdc++.h>using namespace std;#define ll long long#define pb push_back#define fr first#define sc second#define all(x) (x).begin(),(x).end()class Node{public:	int data;	Node *next;	//constructor 	Node(int data){		this->data= data;		this->next = NULL;	}};void insertathead(Node* &head, int a){//here we are using reference head because we want to modify the original linked list	//construct new node	Node *temp= new Node(a);	temp->next = head;	head = temp;}void insertattail(Node* &tail, int a){	Node *temp = new Node(a);	tail->next =temp;	tail = temp;}void insetatmid(Node* &tail, Node* &head , int pos, int a){	if(pos==1){		insertathead(head,a);		return;	}	//point the head at pos-1	Node* temp= head;	int count=1;	while(count<pos-1){		temp = temp->next;		count++;	}	if(temp->next == NULL){		insertattail(tail,a);		return;	}	//inserting at position	Node* inserter= new Node(a);	 inserter->next= temp->next;	 temp->next=inserter;}void print(Node* &head){	Node* temp = head;	while(temp!=NULL){		cout<<temp->data<<" ";		temp= temp->next;	}	cout<<endl;}int main(){	ios_base::sync_with_stdio(false);  	cin.tie(NULL); 	Node* n1= new Node(10);	Node* head= n1;	Node* tail=n1;	print(head);	insertattail(tail,12);	print(head);	insertattail(tail,15);	print(head);	insetatmid(tail,head,4,22);	print(head);	insertattail(tail,32);	print(head);	cout<<"head is at "<<head->data<<endl;	cout<<"tail is at "<<tail->data<<endl;	return 0;}